mapper.insert:
INSERT INTO tbl_member (
    userId, userPw, userName, userEmail, 
    userPhone, userAddress, userPostcode, userBirth
) VALUES (
    #{userId}, #{userPw}, #{userName}, #{userEmail}, 
    #{userPhone}, #{userAddress}, #{userPostcode}, #{userBirth}
)

mapper.select:
SELECT userId, userName
FROM tbl_member
WHERE userId = #{userId}

mapper.update:
UPDATE tbl_member 
SET enabled = #{enabled}, modifyDate = NOW()
WHERE userId = #{userId}

UPDATE tbl_member
SET userEmail = #{userEmail}, userPhone = #{userPhone}, userAddress = #{userAddress}, userBirth = #{userBirth}
WHERE userId = #{userId}
