mapper.select:
SELECT o.*, p.sellerId, p.productName, p.productDesc, p.productStatus, i.imagePath, r.reviewId FROM tbl_order AS o INNER JOIN tbl_product AS p ON o.productId = p.productId LEFT OUTER join tbl_product_image AS i ON p.productId = i.productId LEFT OUTER JOIN tbl_review as r ON r.orderId = o.orderId WHERE o.buyerId = #{userId} AND (i.isMain = 'Y' OR i.isMain IS NULL)
AND ${searchCategory} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY o.regDate DESC LIMIT #{offset}, #{limit}

SELECT o.*, p.sellerId, p.productName, p.productDesc, p.productStatus, i.imagePath FROM tbl_order AS o INNER JOIN tbl_product AS p ON o.productId = p.productId LEFT OUTER join tbl_product_image AS i ON p.productId = i.productId WHERE p.sellerId = #{userId} AND (i.isMain = 'Y' OR i.isMain IS NULL)
AND ${searchCategory} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY o.regDate DESC LIMIT #{offset}, #{limit}
