mapper.select:
SELECT p.productId, p.sellerId, p.productName, p.productDesc, p.price, p.quantity, p.quality, p.productStatus, p.viewCount, p.regDate, p.modifyDate, pi.imageId, pi.imagePath, pi.isMain FROM tbl_product p LEFT JOIN tbl_product_image pi ON p.productId = pi.productId
${searchCategory} LIKE CONCAT('%', #{searchValue}, '%')
AND p.productId = #{productId}
ORDER BY productId DESC LIMIT #{startIdx}, #{pageSize}

SELECT productId, sellerId, productName, productDesc, price, quantity, quality, productStatus, viewCount, regDate, modifyDate
FROM tbl_product
WHERE productId = #{productId}
